import React, { useState } from 'react';
import { Container, Box, Grid, Button, Typography, Paper } from '@mui/material';

const Calculator: React.FC = () => {
  const [display, setDisplay] = useState<string>('');

  const handleButtonClick = (value: string) => {
    if (value === '=') {
      try {
        setDisplay(eval(display).toString());
      } catch {
        setDisplay('Error');
      }
    } else if (value === 'C') {
      setDisplay('');
    } else {
      setDisplay(display + value);
    }
  };

  const buttons = [
    '7', '8', '9', '/', 
    '4', '5', '6', '*', 
    '1', '2', '3', '-', 
    '0', '.', '=', '+', 
    'C'
  ];

  return (
    <Container className="flex justify-center items-center h-screen bg-gray-100">
      <Paper elevation={3} className="p-4 w-80">
        <Typography variant="h4" className="text-center mb-4">Calculator</Typography>
        <Box className="mb-4 p-2 bg-white text-right text-2xl border border-gray-300 rounded">
          {display}
        </Box>
        <Grid container spacing={1}>
          {buttons.map((btn, index) => (
            <Grid item xs={3} key={index}>
              <Button 
                variant="contained" 
                fullWidth 
                className="h-16 text-xl" 
                onClick={() => handleButtonClick(btn)}
              >
                {btn}
              </Button>
            </Grid>
          ))}
        </Grid>
      </Paper>
    </Container>
  );
};

export default Calculator;
